import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import ContactUs from "./components/ContactUs";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isContactModalOpen, setIsContactModalOpen] = useState(false); // ✅ Contact Modal State

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`main-content ${isOpen ? "shifted" : ""}`}>
      <Header toggleSidebar={toggleSidebar} isOpen={isOpen} />
      {/* ✅ Sidebar ko setIsContactModalOpen bhej rahe hain */}
      <Sidebar isOpen={isOpen} toggleSidebar={toggleSidebar} setIsContactModalOpen={setIsContactModalOpen} />
      <ContactUs isOpen={isContactModalOpen} setIsOpen={setIsContactModalOpen} />
      <Footer />
    </div>
  );
};

export default App;
