import React from "react";
import { Link } from "react-router-dom";
import { FaTimes, FaHome, FaInfo, FaServicestack, FaEnvelope } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";

const Sidebar = ({ isOpen, toggleSidebar }) => {
  return (
    <div
      className={`position-fixed top-0 bg-secondary text-white vh-100 p-3 shadow 
      ${isOpen ? "start-0" : "start-100"}`}  // Fixed here
      style={{ width: "250px", transition: "transform 0.3s ease-in-out", transform: isOpen ? "translateX(0)" : "translateX(-100%)" }}
    >
      <button className="btn btn-danger mb-3" onClick={toggleSidebar}>
        <FaTimes />
      </button>
      <ul className="nav flex-column">
        <li className="nav-item">
          <Link to="/" className="nav-link text-white" onClick={toggleSidebar}><FaHome /> Home</Link>
        </li>
        <li className="nav-item">
          <Link to="/about" className="nav-link text-white" onClick={toggleSidebar}><FaInfo /> About</Link>
        </li>
        <li className="nav-item">
          <Link to="/services" className="nav-link text-white" onClick={toggleSidebar}><FaServicestack /> Services</Link>
        </li>
        <li className="nav-item">
          <Link to="/contact" className="nav-link text-white" onClick={toggleSidebar}><FaEnvelope /> Contact</Link>
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
