import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import ContactUs from "./components/ContactUs"; // ✅ ContactUs ko import kiya

const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const closeSidebar = (e) => {
    if (!e.target.closest(".sidebar") && !e.target.closest(".btn-outline-light")) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("click", closeSidebar);
    return () => {
      document.removeEventListener("click", closeSidebar);
    };
  }, []);

  return (
    <Router>
      <div className={`main-content ${isOpen ? "shifted" : ""}`}>
        <Header toggleSidebar={toggleSidebar} isOpen={isOpen} />
        <Sidebar isOpen={isOpen} toggleSidebar={toggleSidebar} />

        {/* ✅ Routes Setup */}
        <div className="content">
          <Routes>
            <Route path="/contact" element={<ContactUs />} /> {/* ✅ Contact Page Linked */}
          </Routes>
        </div>

        {/* ✅ Footer bhi sahi jagah pe aayega */}
        <Footer />
      </div>
    </Router>
  );
};

export default App;
