import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaHome, FaInfo, FaServicestack, FaEnvelope } from "react-icons/fa";
import "./Sidebar.css";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      {/* âœ… Corrected Header Component */}
      <Header toggleSidebar={toggleSidebar} />

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "sidebar-open" : "sidebar-closed"}`}>
        <button className="closebtn" onClick={toggleSidebar}>
          <FaTimes />
        </button>
        <ul>
          <li><Link to="/" onClick={toggleSidebar}><FaHome /> Home</Link></li>
          <li><Link to="/about" onClick={toggleSidebar}><FaInfo /> About</Link></li>
          <li><Link to="/services" onClick={toggleSidebar}><FaServicestack /> Services</Link></li>
          <li><Link to="/contact" onClick={toggleSidebar}><FaEnvelope /> Contact</Link></li>
        </ul>
      </div>
    </div>
  );
};

export default App;
