import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaHome, FaInfo, FaServicestack, FaEnvelope } from "react-icons/fa";
import "./components/Sidebar";
import Header from "./components/Header";  // ✅ Import Header component
const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const closeSidebar = (e) => {
    if (!e.target.closest(".sidebar") && !e.target.closest(".btn-outline-light")) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("click", closeSidebar);
    return () => {
      document.removeEventListener("click", closeSidebar);
    };
  }, []);

  return (
    <div>
      <Header toggleSidebar={toggleSidebar} />

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? "sidebar-open" : "sidebar-closed"}`} onClick={(e) => e.stopPropagation()}>
        <button className="closebtn" onClick={toggleSidebar}>
          <FaTimes />
        </button>
        <ul>
          <li><Link to="/" onClick={toggleSidebar}><FaHome /> Home</Link></li>
          <li><Link to="/about" onClick={toggleSidebar}><FaInfo /> About</Link></li>
          <li><Link to="/services" onClick={toggleSidebar}><FaServicestack /> Services</Link></li>
          <li><Link to="/contact" onClick={toggleSidebar}><FaEnvelope /> Contact</Link></li>
        </ul>
      </div>
    </div>
  );
};

export default App;
